import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

// ‚úÖ Hospital background image
const bgImage =
  "https://images.unsplash.com/photo-1588776814546-7bdf0f1d25ee?auto=format&fit=crop&w=1470&q=80";

// ‚úÖ Global CSS styles
const styles = {
  container: {
    minHeight: "100vh",
    width: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundImage: `url("${bgImage}")`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundAttachment: "fixed",   // ‚úÖ Keeps background fixed on scroll
    fontFamily: "Arial, sans-serif",
  },
  card: {
    background: "rgba(255, 255, 255, 0.9)",
    padding: "40px",
    borderRadius: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
    width: "350px",
    textAlign: "center",
    backdropFilter: "blur(5px)",
  },
  dashboardWrapper: {
    minHeight: "100vh",
    width: "100%",
    padding: "20px",
    backgroundImage: `url("${bgImage}")`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundAttachment: "fixed",
  },
  dashboardContent: {
    maxWidth: "900px",
    margin: "0 auto",
    background: "rgba(255,255,255,0.9)",
    padding: "20px",
    borderRadius: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
  },
  button: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "none",
    fontSize: "16px",
    cursor: "pointer",
  },
  appointmentCard: {
    border: "1px solid #ccc",
    padding: "10px",
    borderRadius: "10px",
    marginBottom: "10px",
    background: "#fff",
  },
};

// üü¢ Auth Component (Login/Register)
function Auth({ onLogin }) {
  const [mode, setMode] = useState("register");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const register = async (e) => {
    e.preventDefault();
    await axios.post(API + "/auth/register", { name, email, password, role });
    alert("Registered Successfully!");
    setMode("login");
  };

  const login = async (e) => {
    e.preventDefault();
    const res = await axios.post(API + "/auth/login", { email, password });
    const u = res.data.user;
    if (u.role !== role) return alert(`Role mismatch: Registered as ${u.role}`);
    localStorage.setItem("token", res.data.token);
    localStorage.setItem("user", JSON.stringify(u));
    onLogin(u);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2>üè• Hospital Management</h2>
        {mode === "register" ? (
          <form onSubmit={register}>
            <input
              style={styles.input}
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              style={styles.input}
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              style={styles.input}
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <select
              style={styles.input}
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button
              style={{ ...styles.button, background: "#28a745", color: "#fff" }}
            >
              Register
            </button>
            <span
              style={{ color: "#007bff", cursor: "pointer" }}
              onClick={() => setMode("login")}
            >
              Already registered? Login
            </span>
          </form>
        ) : (
          <form onSubmit={login}>
            <input
              style={styles.input}
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              style={styles.input}
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <select
              style={styles.input}
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button
              style={{ ...styles.button, background: "#007bff", color: "#fff" }}
            >
              Login
            </button>
            <span
              style={{ color: "#007bff", cursor: "pointer" }}
              onClick={() => setMode("register")}
            >
              Need to Register?
            </span>
          </form>
        )}
      </div>
    </div>
  );
}

// üü° Dashboard Wrapper (Employee / Doctor / HR content same jaise tha)

export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const u = JSON.parse(localStorage.getItem("user"));
    if (token && u) setUser(u);
  }, []);

  const logout = () => {
    localStorage.clear();
    setUser(null);
  };

  if (!user) return <Auth onLogin={setUser} />;

  // ‚úçÔ∏è Aapka EmployeeDashboard, DoctorDashboard, HRDashboard niche same rakhiye (unchanged)
  return <div>Dashboard Components Below...</div>;
}
