import React, { useState, useEffect } from "react";
import Auth from "./Auth"; // Login/Register component
import EmployeeDashboard from "./EmployeeDashboard";
import DoctorDashboard from "./DoctorDashboard";

export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const u = JSON.parse(localStorage.getItem("user"));
    if (token && u) setUser(u);
  }, []);

  const logout = () => {
    localStorage.clear();
    setUser(null);
  };

  if (!user) return <Auth onLogin={setUser} />;

  // Role-based dashboard
  if (user.role === "employee") return <EmployeeDashboard user={user} logout={logout} />;
  if (user.role === "doctor") return <DoctorDashboard user={user} logout={logout} />;

  return <div>Role not recognized</div>;
}
