import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

// Hospital background image
const bgImage = "https://images.unsplash.com/photo-1588776814546-7bdf0f1d25ee?auto=format&fit=crop&w=1470&q=80";

// --- Auth Component (Register/Login) ---
function Auth({ onLogin }) {
  const [mode, setMode] = useState("register");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!name || !email || !password || !role) return alert("Fill all fields");
    try {
      await axios.post(API + "/auth/register", { name, email, password, role });
      alert("Registered successfully! Now login.");
      setMode("login");
      setName(""); setEmail(""); setPassword(""); setRole("");
    } catch (err) {
      alert(err.response?.data?.msg || "Register failed");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!email || !password || !role) return alert("Fill all fields");
    try {
      const res = await axios.post(API + "/auth/login", { email, password });
      const u = res.data.user;
      if (u.role !== role) return alert(`Selected role mismatch: ${u.role}`);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(u));
      onLogin(u);
    } catch (err) {
      alert(err.response?.data?.msg || "Login failed");
    }
  };

  return (
    <div
      className="min-h-screen flex items-center justify-center bg-cover bg-center"
      style={{ backgroundImage: `url(${bgImage})` }}
    >
      <div className="bg-white/80 backdrop-blur-md p-10 rounded-3xl shadow-2xl w-full max-w-md space-y-6">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-4">
          üè• Hospital Management
        </h2>

        {mode === "register" ? (
          <form onSubmit={handleRegister} className="space-y-4">
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            >
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button
              type="submit"
              className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition-colors"
            >
              Register
            </button>
            <p
              className="text-center text-blue-600 cursor-pointer hover:underline"
              onClick={() => setMode("login")}
            >
              Already registered? Login
            </p>
          </form>
        ) : (
          <form onSubmit={handleLogin} className="space-y-4">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            >
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors"
            >
              Login
            </button>
            <p
              className="text-center text-green-600 cursor-pointer hover:underline"
              onClick={() => setMode("register")}
            >
              Need to register? Sign up
            </p>
          </form>
        )}
      </div>
    </div>
  );
}

// --- Dashboard Wrapper with hospital background ---
function DashboardWrapper({ user, onLogout, children }) {
  return (
    <div
      className="min-h-screen bg-cover bg-center p-6 flex flex-col"
      style={{ backgroundImage: `url(${bgImage})` }}
    >
      <div className="flex justify-between items-center mb-6 max-w-6xl mx-auto bg-white/80 p-4 rounded-2xl shadow backdrop-blur-md">
        <h1 className="text-3xl font-bold text-gray-800">
          üè• Welcome, {user.name} ({user.role})
        </h1>
        <button
          onClick={onLogout}
          className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
        >
          Logout
        </button>
      </div>
      <div className="max-w-6xl mx-auto bg-white/80 p-6 rounded-2xl shadow backdrop-blur-md flex-1">
        {children}
      </div>
    </div>
  );
}

export default Auth;
