// src/dashboards/HRDashboard.js
import React, { useState, useEffect } from "react";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "220px",
    background: "#6f42c1",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
  },
  sidebarItem: (active) => ({
    padding: "15px 20px",
    cursor: "pointer",
    background: active ? "#593196" : "transparent",
    margin: "5px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  total: { fontSize: "18px", fontWeight: "bold", marginBottom: "15px" },
  button: { padding: "6px 12px", borderRadius: "5px", cursor: "pointer", marginTop: "10px", border: "none" },
  logoutBtn: { padding: "8px 12px", marginTop: "20px", borderRadius: "5px", cursor: "pointer", background: "#dc3545", color: "#fff", border: "none" },
};

export default function HRDashboard({ user, onLogout }) {
  const [selected, setSelected] = useState("employees");
  const [employees, setEmployees] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = () => {
    const allUsers = JSON.parse(localStorage.getItem("users") || "[]");
    setEmployees(allUsers.filter(u => u.role === "employee"));

    const att = JSON.parse(localStorage.getItem("attendance") || "[]");
    setAttendance(att);

    const apps = JSON.parse(localStorage.getItem("appointments") || "[]");
    setAppointments(apps);
  };

  const verifyAppointment = (index) => {
    const apps = [...appointments];
    apps[index].verified = true;
    localStorage.setItem("appointments", JSON.stringify(apps));
    setAppointments(apps);
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{ textAlign: "center" }}>HR Dashboard</h2>
        <div style={styles.sidebarItem(selected === "employees")} onClick={() => setSelected("employees")}>Employees</div>
        <div style={styles.sidebarItem(selected === "attendance")} onClick={() => setSelected("attendance")}>Attendance</div>
        <div style={styles.sidebarItem(selected === "appointments")} onClick={() => setSelected("appointments")}>Appointments</div>
        <button style={styles.logoutBtn} onClick={onLogout}>Logout</button>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {selected === "employees" && (
          <div>
            <div style={styles.total}>Total Employees: {employees.length}</div>
            {employees.map((emp, i) => (
              <div key={i} style={styles.card}>
                <p><strong>Name:</strong> {emp.name}</p>
                <p><strong>Email:</strong> {emp.email}</p>
                <p><strong>Role:</strong> {emp.role}</p>
              </div>
            ))}
          </div>
        )}

        {selected === "attendance" && (
          <div>
            <h3>All Attendance Records</h3>
            {attendance.length === 0 ? <p>No attendance found</p> : 
              attendance.map((a, i) => (
                <div key={i} style={styles.card}>
                  <p><strong>Employee Email:</strong> {a.employeeEmail}</p>
                  <p><strong>Date:</strong> {a.date}</p>
                  <p><strong>Status:</strong> {a.status}</p>
                </div>
              ))
            }
          </div>
        )}

        {selected === "appointments" && (
          <div>
            <h3>All Appointments</h3>
            {appointments.length === 0 ? <p>No appointments found</p> :
              appointments.map((a, i) => (
                <div key={i} style={styles.card}>
                  <p><strong>Employee:</strong> {a.employeeEmail}</p>
                  <p><strong>Doctor:</strong> {a.doctor}</p>
                  <p><strong>Date & Time:</strong> {new Date(a.datetime).toLocaleString()}</p>
                  <p><strong>Verified:</strong> {a.verified ? "✅ Yes" : "❌ No"}</p>
                  {!a.verified && (
                    <button style={{ ...styles.button, background: "#28a745", color: "#fff" }} onClick={() => verifyAppointment(i)}>
                      Verify
                    </button>
                  )}
                </div>
              ))
            }
          </div>
        )}
      </div>
    </div>
  );
}
