// src/dashboards/HRDashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "220px",
    background: "#6f42c1",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
  },
  sidebarItem: (active) => ({
    padding: "15px 20px",
    cursor: "pointer",
    background: active ? "#593196" : "transparent",
    margin: "5px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  total: { fontSize: "18px", fontWeight: "bold", marginBottom: "15px" },
  button: { padding: "6px 12px", borderRadius: "5px", cursor: "pointer", marginLeft: "10px", border: "none" },
  logoutBtn: { padding: "8px 12px", marginTop: "20px", borderRadius: "5px", cursor: "pointer", background: "#dc3545", color: "#fff", border: "none" },
};

export default function HRDashboard({ user, onLogout }) {
  const [selected, setSelected] = useState("employees");
  const [employees, setEmployees] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetchEmployees();
    fetchAttendance();
    fetchAppointments();
  }, []);

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      const res = await axios.get(`${API}/users`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      // Filter only employees
      const employeeList = res.data.filter(u => u.role === "employee");
      setEmployees(employeeList);
    } catch (err) {
      console.error("Failed to fetch employees:", err);
    }
  };

  // Fetch attendance records
  const fetchAttendance = async () => {
    try {
      const res = await axios.get(`${API}/employee/attendance`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAttendance(res.data);
    } catch (err) {
      console.error("Failed to fetch attendance:", err);
    }
  };

  // Fetch appointments
  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/employee/appointments`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAppointments(res.data);
    } catch (err) {
      console.error("Failed to fetch appointments:", err);
    }
  };

  const verifyAppointment = async (apptId) => {
    try {
      const res = await axios.put(`${API}/appointments/${apptId}/verify`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAppointments(appointments.map(a => a._id === apptId ? res.data : a));
      alert("Appointment verified!");
    } catch (err) {
      console.error("Failed to verify appointment:", err);
      alert("Failed to verify appointment");
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{ textAlign: "center" }}>HR</h2>
        <div style={styles.sidebarItem(selected === "employees")} onClick={() => setSelected("employees")}>Employees</div>
        <div style={styles.sidebarItem(selected === "attendance")} onClick={() => setSelected("attendance")}>Attendance</div>
        <div style={styles.sidebarItem(selected === "appointments")} onClick={() => setSelected("appointments")}>Appointments</div>
        <button style={styles.logoutBtn} onClick={onLogout}>Logout</button>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {selected === "employees" && (
          <div>
            <div style={styles.total}>Total Employees: {employees.length}</div>
            {employees.length === 0 ? <p>No employees found</p> :
              employees.map(emp => (
                <div key={emp._id} style={styles.card}>
                  <p><strong>Name:</strong> {emp.name}</p>
                  <p><strong>Email:</strong> {emp.email}</p>
                  <p><strong>Role:</strong> {emp.role}</p>
                </div>
              ))
            }
          </div>
        )}

        {selected === "attendance" && (
          <div>
            <h3>Attendance Records</h3>
            {attendance.length === 0 ? <p>No records</p> :
              attendance.map(a => (
                <div key={a._id} style={styles.card}>
                  <p><strong>Employee:</strong> {a.employee?.name || "Unknown"}</p>
                  <p><strong>Date:</strong> {a.date}</p>
                  <p><strong>Status:</strong> {a.status}</p>
                </div>
              ))
            }
          </div>
        )}

        {selected === "appointments" && (
          <div>
            <h3>Appointments</h3>
            {appointments.length === 0 ? <p>No appointments</p> :
              appointments.map(a => (
                <div key={a._id} style={styles.card}>
                  <p><strong>Employee:</strong> {a.employee?.name || "Unknown"}</p>
                  <p><strong>Doctor:</strong> {a.doctor?.name || "Unknown"}</p>
                  <p><strong>Date & Time:</strong> {new Date(a.datetime).toLocaleString()}</p>
                  <p><strong>Verified:</strong> {a.verified ? "Yes" : "No"}</p>
                  {!a.verified && (
                    <button
                      style={{ ...styles.button, background: "#28a745", color: "#fff" }}
                      onClick={() => verifyAppointment(a._id)}
                    >
                      Verify
                    </button>
                  )}
                </div>
              ))
            }
          </div>
        )}
      </div>
    </div>
  );
}
