import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

// --- Login Component ---
function Login({ onLogin }) {
  const [role, setRole] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!role || !email || !password) {
      alert("Please fill all fields and select a role");
      return;
    }
    try {
      const res = await axios.post(API + "/auth/login", { email, password });
      const user = res.data.user;
      if (user.role !== role) {
        alert(`Selected role does not match your account role: ${user.role}`);
        return;
      }
      localStorage.setItem("token", res.data.token);
      onLogin(user);
    } catch (err) {
      alert(err.response?.data?.msg || "Login failed");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-blue-200 p-4">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md space-y-4"
      >
        <h2 className="text-2xl font-bold text-center text-gray-800">
          üè• Hospital Login
        </h2>

        {/* Role Selection */}
        <div>
          <label className="block mb-1 text-gray-600 font-medium">
            Select Role
          </label>
          <select
            value={role}
            onChange={(e) => setRole(e.target.value)}
            className="w-full border rounded-lg px-3 py-2 outline-none focus:ring-2 focus:ring-blue-400"
          >
            <option value="">-- Choose Role --</option>
            <option value="doctor">Doctor</option>
            <option value="employee">Employee</option>
            <option value="hr">HR</option>
          </select>
        </div>

        {/* Email */}
        <div>
          <label className="block mb-1 text-gray-600 font-medium">
            Email / Username
          </label>
          <input
            type="text"
            placeholder="Enter your email or username"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full border rounded-lg px-3 py-2 outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>

        {/* Password */}
        <div>
          <label className="block mb-1 text-gray-600 font-medium">
            Password
          </label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full border rounded-lg px-3 py-2 outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>

        {/* Login Button */}
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition"
        >
          Login
        </button>

        <p className="text-center text-sm text-gray-500 mt-2">
          For demo: register users via backend /api/auth/register
        </p>
      </form>
    </div>
  );
}

// --- Employee Dashboard ---
function EmployeeDashboard({ user }) {
  const [appts, setAppts] = useState([]);
  const [date, setDate] = useState("");
  const [reason, setReason] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    const res = await axios.get(API + "/appointments", {
      headers: { Authorization: "Bearer " + token },
    });
    setAppts(res.data.filter((a) => a.employee?._id === user._id));
  };

  const bookAppointment = async (e) => {
    e.preventDefault();
    await axios.post(
      API + "/appointments",
      { date, reason },
      { headers: { Authorization: "Bearer " + token } }
    );
    setDate("");
    setReason("");
    fetchAppointments();
  };

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Employee Dashboard</h2>

      <form onSubmit={bookAppointment} className="mb-6 space-y-2">
        <input
          type="datetime-local"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border rounded px-3 py-2 w-full"
          required
        />
        <input
          type="text"
          placeholder="Reason"
          value={reason}
          onChange={(e) => setReason(e.target.value)}
          className="border rounded px-3 py-2 w-full"
        />
        <button className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
          Book Appointment
        </button>
      </form>

      <h3 className="text-xl font-semibold mb-2">Your Appointments</h3>
      <ul className="space-y-1">
        {appts.map((a) => (
          <li
            key={a._id}
            className="border p-2 rounded flex justify-between items-center"
          >
            <span>
              {new Date(a.date).toLocaleString()} - {a.reason} - Verified:{" "}
              {String(a.verified)}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
}

// --- Doctor Dashboard ---
function DoctorDashboard({ user }) {
  const [appts, setAppts] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    const res = await axios.get(API + "/appointments", {
      headers: { Authorization: "Bearer " + token },
    });
    setAppts(res.data);
  };

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Doctor Dashboard</h2>
      <ul className="space-y-1">
        {appts.map((a) => (
          <li
            key={a._id}
            className="border p-2 rounded flex justify-between items-center"
          >
            <span>
              {new Date(a.date).toLocaleString()} - Employee:{" "}
              {a.employee?.name || "N/A"} - {a.reason}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
}

// --- HR Dashboard ---
function HRDashboard({ user }) {
  const [appts, setAppts] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    const res = await axios.get(API + "/appointments", {
      headers: { Authorization: "Bearer " + token },
    });
    setAppts(res.data);
  };

  const verifyAppointment = async (id) => {
    await axios.put(API + "/appointments/" + id + "/verify", {}, {
      headers: { Authorization: "Bearer " + token },
    });
    fetchAppointments();
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">HR Dashboard</h2>
      <ul className="space-y-2">
        {appts.map((a) => (
          <li
            key={a._id}
            className="border p-2 rounded flex justify-between items-center"
          >
            <span>
              {new Date(a.date).toLocaleString()} - Employee: {a.employee?.name}{" "}
              - Verified: {String(a.verified)}
            </span>
            {!a.verified && (
              <button
                onClick={() => verifyAppointment(a._id)}
                className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
              >
                Verify
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

// --- Main App ---
export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = JSON.parse(localStorage.getItem("user"));
    if (token && userData) setUser(userData);
  }, []);

  const handleLogin = (u) => {
    localStorage.setItem("user", JSON.stringify(u));
    setUser(u);
  };

  if (!user) return <Login onLogin={handleLogin} />;

  switch (user.role) {
    case "employee":
      return <EmployeeDashboard user={user} />;
    case "doctor":
      return <DoctorDashboard user={user} />;
    case "hr":
      return <HRDashboard user={user} />;
    default:
      return <div>Unknown role</div>;
  }
}
