import React, { useEffect, useState } from "react";
import axios from "axios";

const EmployeeDashboard = () => {
  const [attendance, setAttendance] = useState([]);

  useEffect(() => {
    fetchAttendance();
  }, []);

  const fetchAttendance = async () => {
    try {
      const res = await axios.get("/api/employee/attendance", {
        headers: { "x-auth-token": localStorage.getItem("token") },
      });
      setAttendance(res.data);
    } catch (err) {
      console.error("Error loading attendance:", err);
    }
  };

  const today = new Date().toISOString().split("T")[0];
  const todayRecord = attendance.find((a) => a.date === today);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>Employee Dashboard</h2>

      {todayRecord ? (
        <h3>
          Today's Attendance:{" "}
          <span
            style={{
              color: todayRecord.status === "present" ? "green" : "red",
            }}
          >
            {todayRecord.status.toUpperCase()}
          </span>
        </h3>
      ) : (
        <h3>Today's Attendance: Not marked yet</h3>
      )}

      <hr />

      <h3>Past Attendance</h3>
      {attendance.map((a) => (
        <div key={a._id} style={{ marginBottom: "10px" }}>
          <p>
            {a.date} -{" "}
            <span
              style={{
                color: a.status === "present" ? "green" : "red",
              }}
            >
              {a.status.toUpperCase()}
            </span>
          </p>
        </div>
      ))}
    </div>
  );
};

export default EmployeeDashboard;
