import React, { useState } from "react";
import axios from "axios";
import bgImage from "./assets/hospital-bg.png"; // üè• your background image

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: {
    minHeight: "100vh",
    width: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundImage: `url(${bgImage})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    fontFamily: "Arial, sans-serif",
  },
  card: {
    background: "rgba(255, 255, 255, 0.9)",
    padding: "40px",
    borderRadius: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
    width: "350px",
    textAlign: "center",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
  },
  button: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "none",
    fontSize: "16px",
    cursor: "pointer",
  },
};

function Auth({ onLogin }) {
  const [mode, setMode] = useState("login");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const register = async (e) => {
    e.preventDefault();
    if (!name || !email || !password || !role) return alert("All fields required");
    try {
      const res = await axios.post(`${API}/auth/register`, {
        name,
        email,
        password,
        role,
      });
      alert("Registered successfully!");
      setMode("login");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.msg || "Registration failed");
    }
  };

  const login = async (e) => {
    e.preventDefault();
    if (!email || !password) return alert("Email & password required");
    try {
      const res = await axios.post(`${API}/auth/login`, { email, password });
      const u = res.data.user;
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(u));
      onLogin(u);
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.msg || "Login failed");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2>üè• Hospital Management</h2>
        {mode === "register" ? (
          <form onSubmit={register}>
            <input
              style={styles.input}
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              style={styles.input}
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              style={styles.input}
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <select
              style={styles.input}
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button
              style={{ ...styles.button, background: "#28a745", color: "#fff" }}
            >
              Register
            </button>
            <p
              style={{ cursor: "pointer", color: "#007bff" }}
              onClick={() => setMode("login")}
            >
              Already registered? Login
            </p>
          </form>
        ) : (
          <form onSubmit={login}>
            <input
              style={styles.input}
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              style={styles.input}
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button
              style={{ ...styles.button, background: "#007bff", color: "#fff" }}
            >
              Login
            </button>
            <p
              style={{ cursor: "pointer", color: "#007bff" }}
              onClick={() => setMode("register")}
            >
              Need to Register?
            </p>
          </form>
        )}
      </div>
    </div>
  );
}

export default Auth;
