import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "220px",
    background: "#6f42c1",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
  },
  sidebarItem: (active) => ({
    padding: "15px 20px",
    cursor: "pointer",
    background: active ? "#593196" : "transparent",
    margin: "5px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  total: { fontSize: "18px", fontWeight: "bold", marginBottom: "15px" },
  button: { padding: "6px 12px", borderRadius: "5px", cursor: "pointer", marginLeft: "10px", border: "none" },
  logoutBtn: {
    padding: "8px 12px",
    marginTop: "20px",
    borderRadius: "5px",
    cursor: "pointer",
    background: "#dc3545",
    color: "#fff",
    border: "none",
  },
};

export default function HRDashboard({ user, onLogout }) {
  const [selected, setSelected] = useState("employees");
  const [employees, setEmployees] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetchEmployees();
    fetchDoctors();
    fetchAttendance();
    fetchAppointments();
  }, []);

  // ✅ Fetch all employees
  const fetchEmployees = async () => {
    try {
      const res = await axios.get(`${API}/hr/employees`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setEmployees(res.data);
    } catch (err) {
      console.error("Failed to fetch employees:", err);
    }
  };

  // ✅ Fetch all doctors
  const fetchDoctors = async () => {
    try {
      const res = await axios.get(`${API}/hr/doctors`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setDoctors(res.data);
    } catch (err) {
      console.error("Failed to fetch doctors:", err);
    }
  };

  // ✅ Fetch all attendance
  const fetchAttendance = async () => {
    try {
      const res = await axios.get(`${API}/hr/attendance`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAttendance(res.data);
    } catch (err) {
      console.error("Failed to fetch attendance:", err);
    }
  };

  // ✅ Mark attendance as verified (Present/Absent)
  const verifyAttendance = async (id, status) => {
    try {
      const res = await axios.put(
        `${API}/hr/attendance/${id}`,
        { status },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      setAttendance(attendance.map(a => (a._id === id ? res.data : a)));
      alert(`Attendance marked as ${status}`);
    } catch (err) {
      console.error("Failed to verify attendance:", err);
    }
  };

  // ✅ Fetch all appointments
  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/hr/appointments`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAppointments(res.data);
    } catch (err) {
      console.error("Failed to fetch appointments:", err);
    }
  };

  // ✅ Verify appointment
  const verifyAppointment = async (id) => {
    try {
      const res = await axios.put(
        `${API}/hr/appointments/${id}/verify`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      setAppointments(appointments.map(a => (a._id === id ? res.data : a)));
      alert("Appointment verified");
    } catch (err) {
      console.error("Failed to verify appointment:", err);
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{ textAlign: "center" }}>HR Panel</h2>
        <div style={styles.sidebarItem(selected === "employees")} onClick={() => setSelected("employees")}>
          Employees
        </div>
        <div style={styles.sidebarItem(selected === "doctors")} onClick={() => setSelected("doctors")}>
          Doctors
        </div>
        <div style={styles.sidebarItem(selected === "attendance")} onClick={() => setSelected("attendance")}>
          Attendance
        </div>
        <div style={styles.sidebarItem(selected === "appointments")} onClick={() => setSelected("appointments")}>
          Appointments
        </div>
        <button style={styles.logoutBtn} onClick={onLogout}>Logout</button>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {/* Employees */}
        {selected === "employees" && (
          <div>
            <div style={styles.total}>Total Employees: {employees.length}</div>
            {employees.map(emp => (
              <div key={emp._id} style={styles.card}>
                <p><strong>Name:</strong> {emp.name}</p>
                <p><strong>Email:</strong> {emp.email}</p>
                <p><strong>Department:</strong> {emp.department || "N/A"}</p>
                <p><strong>Role:</strong> {emp.role}</p>
              </div>
            ))}
          </div>
        )}

        {/* Doctors */}
        {selected === "doctors" && (
          <div>
            <div style={styles.total}>Total Doctors: {doctors.length}</div>
            {doctors.map(doc => (
              <div key={doc._id} style={styles.card}>
                <p><strong>Name:</strong> {doc.name}</p>
                <p><strong>Email:</strong> {doc.email}</p>
                <p><strong>Specialization:</strong> {doc.specialization || "N/A"}</p>
                <p><strong>Role:</strong> {doc.role}</p>
              </div>
            ))}
          </div>
        )}

        {/* Attendance */}
        {selected === "attendance" && (
          <div>
            <div style={styles.total}>Attendance Records: {attendance.length}</div>
            {attendance.map(a => (
              <div key={a._id} style={styles.card}>
                <p><strong>Employee:</strong> {a.employee?.name}</p>
                <p><strong>Date:</strong> {new Date(a.date).toLocaleDateString()}</p>
                <p><strong>Status:</strong> {a.status}</p>
                <button
                  style={{ ...styles.button, background: "#28a745", color: "#fff" }}
                  onClick={() => verifyAttendance(a._id, "Present")}
                >
                  Present
                </button>
                <button
                  style={{ ...styles.button, background: "#dc3545", color: "#fff" }}
                  onClick={() => verifyAttendance(a._id, "Absent")}
                >
                  Absent
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Appointments */}
        {selected === "appointments" && (
          <div>
            <div style={styles.total}>Appointments: {appointments.length}</div>
            {appointments.map(a => (
              <div key={a._id} style={styles.card}>
                <p><strong>Employee:</strong> {a.employeeId?.name}</p>
                <p><strong>Doctor:</strong> {a.doctorId?.name}</p>
                <p><strong>Date & Time:</strong> {new Date(a.datetime).toLocaleString()}</p>
                <p><strong>Verified:</strong> {a.verified ? "✅ Yes" : "❌ No"}</p>
                {!a.verified && (
                  <button
                    style={{ ...styles.button, background: "#007bff", color: "#fff" }}
                    onClick={() => verifyAppointment(a._id)}
                  >
                    Verify
                  </button>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
