import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

// ‚úÖ Hospital background image
const bgImage =
  "https://images.unsplash.com/photo-1588776814546-7bdf0f1d25ee?auto=format&fit=crop&w=1470&q=80";

// ‚úÖ Global CSS styles
const styles = {
  container: {
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundImage: `url(${bgImage})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    fontFamily: "Arial, sans-serif",
  },
  card: {
    background: "rgba(255, 255, 255, 0.9)",
    padding: "40px",
    borderRadius: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
    width: "350px",
    textAlign: "center",
    backdropFilter: "blur(5px)",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
  },
  button: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "none",
    fontSize: "16px",
    cursor: "pointer",
  },
  dashboardWrapper: {
    minHeight: "100vh",
    padding: "20px",
    backgroundImage: `url(${bgImage})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
  },
  dashboardContent: {
    maxWidth: "900px",
    margin: "0 auto",
    background: "rgba(255,255,255,0.9)",
    padding: "20px",
    borderRadius: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
  },
  appointmentCard: {
    border: "1px solid #ccc",
    padding: "10px",
    borderRadius: "10px",
    marginBottom: "10px",
    background: "#fff",
  },
};

// ‚úÖ Login & Register Page
function Auth({ onLogin }) {
  const [mode, setMode] = useState("register");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const register = async (e) => {
    e.preventDefault();
    await axios.post(API + "/auth/register", { name, email, password, role });
    alert("Registered Successfully!");
    setMode("login");
  };

  const login = async (e) => {
    e.preventDefault();
    const res = await axios.post(API + "/auth/login", { email, password });
    const u = res.data.user;
    if (u.role !== role) return alert(`Role mismatch: Registered as ${u.role}`);
    localStorage.setItem("token", res.data.token);
    localStorage.setItem("user", JSON.stringify(u));
    onLogin(u);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2>üè• Hospital Management</h2>
        {mode === "register" ? (
          <form onSubmit={register}>
            <input style={styles.input} placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
            <input style={styles.input} type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input style={styles.input} type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <select style={styles.input} value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button style={{ ...styles.button, background: "#28a745", color: "#fff" }}>Register</button>
            <span style={{ color: "#007bff", cursor: "pointer" }} onClick={() => setMode("login")}>
              Already registered? Login
            </span>
          </form>
        ) : (
          <form onSubmit={login}>
            <input style={styles.input} type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input style={styles.input} type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <select style={styles.input} value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button style={{ ...styles.button, background: "#007bff", color: "#fff" }}>Login</button>
            <span style={{ color: "#007bff", cursor: "pointer" }} onClick={() => setMode("register")}>
              Need to Register?
            </span>
          </form>
        )}
      </div>
    </div>
  );
}

// ‚úÖ Dashboard Wrapper
function DashboardWrapper({ user, onLogout, children }) {
  return (
    <div style={styles.dashboardWrapper}>
      <div style={{ display: "flex", justifyContent: "space-between", maxWidth: "900px", margin: "0 auto" }}>
        <h1>Welcome, {user.name} ({user.role})</h1>
        <button onClick={onLogout} style={{ ...styles.button, background: "#dc3545", color: "#fff", width: "auto", padding: "10px 20px" }}>
          Logout
        </button>
      </div>
      <div style={styles.dashboardContent}>{children}</div>
    </div>
  );
}

// ‚úÖ Employee Dashboard
function EmployeeDashboard({ user }) {
  const [appts, setAppts] = useState([]);
  const [medicines, setMedicines] = useState([]);
  const [date, setDate] = useState("");
  const [reason, setReason] = useState("");
  const token = localStorage.getItem("token");

  const fetchData = async () => {
    const resA = await axios.get(API + "/appointments", { headers: { Authorization: `Bearer ${token}` } });
    setAppts(resA.data.filter(a => a.employee?._id === user._id));

    const resM = await axios.get(API + "/medicines", { headers: { Authorization: `Bearer ${token}` } });
    setMedicines(resM.data.filter(m => m.employee === user._id));
  };

  useEffect(() => { fetchData(); }, []);

  const book = async (e) => {
    e.preventDefault();
    await axios.post(API + "/appointments", { date, reason }, { headers: { Authorization: `Bearer ${token}` } });
    setDate(""); setReason(""); fetchData();
  };

  return (
    <div>
      <h2>Employee Dashboard</h2>
      <form onSubmit={book}>
        <input style={styles.input} type="datetime-local" value={date} onChange={(e) => setDate(e.target.value)} required />
        <input style={styles.input} placeholder="Reason" value={reason} onChange={(e) => setReason(e.target.value)} />
        <button style={{ ...styles.button, background: "#28a745", color: "#fff" }}>Book Appointment</button>
      </form>
      <h3>Your Appointments</h3>
      {appts.map((a) => (
        <div key={a._id} style={styles.appointmentCard}>
          {new Date(a.date).toLocaleString()} - {a.reason} - Verified: {String(a.verified)}
        </div>
      ))}
      <h3>Your Medicines</h3>
      {medicines.map((m) => (
        <div key={m._id} style={styles.appointmentCard}>
          {m.name} - {m.dosage}
        </div>
      ))}
    </div>
  );
}

// ‚úÖ Doctor Dashboard ‚Äî Add Medicines
function DoctorDashboard() {
  const [appts, setAppts] = useState([]);
  const [empId, setEmpId] = useState("");
  const [name, setName] = useState("");
  const [dosage, setDosage] = useState("");
  const token = localStorage.getItem("token");

  const fetchAppointments = async () => {
    const res = await axios.get(API + "/appointments", { headers: { Authorization: `Bearer ${token}` } });
    setAppts(res.data);
  };

  useEffect(() => { fetchAppointments(); }, []);

  const addMedicine = async (e) => {
    e.preventDefault();
    await axios.post(API + "/medicines", { employee: empId, name, dosage }, { headers: { Authorization: `Bearer ${token}` } });
    setName(""); setDosage(""); setEmpId(""); fetchAppointments();
  };

  return (
    <div>
      <h2>Doctor Dashboard</h2>
      <h3>Appointments</h3>
      {appts.map((a) => (
        <div key={a._id} style={styles.appointmentCard}>
          {new Date(a.date).toLocaleString()} ‚Äî {a.employee?.name} ‚Äî {a.reason}
          <button
            style={{ ...styles.button, background: "#007bff", color: "#fff", width: "auto", marginTop: "5px" }}
            onClick={() => setEmpId(a.employee?._id)}
          >
            Add Medicine
          </button>
        </div>
      ))}

      {empId && (
        <form onSubmit={addMedicine}>
          <h4>Add Medicine</h4>
          <input style={styles.input} placeholder="Medicine Name" value={name} onChange={(e) => setName(e.target.value)} />
          <input style={styles.input} placeholder="Dosage" value={dosage} onChange={(e) => setDosage(e.target.value)} />
          <button style={{ ...styles.button, background: "#28a745", color: "#fff" }}>Save</button>
        </form>
      )}
    </div>
  );
}

// ‚úÖ HR Dashboard ‚Äî Verify appointments & medicines
function HRDashboard() {
  const [appts, setAppts] = useState([]);
  const [medicines, setMedicines] = useState([]);
  const token = localStorage.getItem("token");

  const fetchData = async () => {
    const resA = await axios.get(API + "/appointments", { headers: { Authorization: `Bearer ${token}` } });
    setAppts(resA.data);

    const resM = await axios.get(API + "/medicines", { headers: { Authorization: `Bearer ${token}` } });
    setMedicines(resM.data);
  };

  useEffect(() => { fetchData(); }, []);

  const verifyAppt = async (id) => {
    await axios.put(API + `/appointments/${id}/verify`, {}, { headers: { Authorization: `Bearer ${token}` } });
    fetchData();
  };

  return (
    <div>
      <h2>HR Dashboard</h2>
      <h3>Appointments</h3>
      {appts.map((a) => (
        <div key={a._id} style={styles.appointmentCard}>
          {new Date(a.date).toLocaleString()} ‚Äî {a.employee?.name} ‚Äî Verified: {String(a.verified)}
          {!a.verified && (
            <button onClick={() => verifyAppt(a._id)} style={{ ...styles.button, background: "#007bff", color: "#fff", width: "auto", marginTop: "5px" }}>
              Verify
            </button>
          )}
        </div>
      ))}

      <h3>Medicines</h3>
      {medicines.map((m) => (
        <div key={m._id} style={styles.appointmentCard}>
          {m.name} ‚Äî {m.dosage} ‚Äî For: {m.employee?.name}
        </div>
      ))}
    </div>
  );
}

// ‚úÖ Main App
export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const u = JSON.parse(localStorage.getItem("user"));
    if (token && u) setUser(u);
  }, []);

  const logout = () => {
    localStorage.clear();
    setUser(null);
  };

  if (!user) return <Auth onLogin={setUser} />;

  switch (user.role) {
    case "employee":
      return <DashboardWrapper user={user} onLogout={logout}><EmployeeDashboard user={user} /></DashboardWrapper>;
    case "doctor":
      return <DashboardWrapper user={user} onLogout={logout}><DoctorDashboard user={user} /></DashboardWrapper>;
    case "hr":
      return <DashboardWrapper user={user} onLogout={logout}><HRDashboard user={user} /></DashboardWrapper>;
    default:
      return <div>Unknown Role</div>;
  }
}
