import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "220px",
    background: "#17a2b8",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
  },
  sidebarItem: (active) => ({
    padding: "15px 20px",
    cursor: "pointer",
    background: active ? "#117a8b" : "transparent",
    margin: "5px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  button: { padding: "8px 12px", marginLeft: "10px", borderRadius: "5px", cursor: "pointer", background: "#17a2b8", color: "#fff", border: "none" },
  logoutBtn: { padding: "8px 12px", marginTop: "20px", borderRadius: "5px", cursor: "pointer", background: "#dc3545", color: "#fff", border: "none" },
};

export default function HRDashboard({ user, onLogout }) {
  const [selected, setSelected] = useState("attendance"); // attendance / appointments
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetchAttendance();
    fetchAppointments();
  }, []);

  // --- Fetch Attendance ---
  const fetchAttendance = async () => {
    try {
      const res = await axios.get(`${API}/hr/attendance`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAttendance(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // --- Fetch Appointments ---
  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/employee/appointments`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // --- Verify Appointment ---
  const verifyAppointment = async (id) => {
    try {
      await axios.put(`${API}/employee/appointments/verify/${id}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      alert("Appointment verified!");
      fetchAppointments(); // refresh list
    } catch (err) {
      console.error(err);
      alert("Failed to verify");
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{ textAlign: "center" }}>HR</h2>
        <div style={styles.sidebarItem(selected === "attendance")} onClick={() => setSelected("attendance")}>Attendance</div>
        <div style={styles.sidebarItem(selected === "appointments")} onClick={() => setSelected("appointments")}>Appointments</div>
        <button style={styles.logoutBtn} onClick={onLogout}>Logout</button>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {selected === "attendance" && (
          <>
            <h3>Employee Attendance</h3>
            {attendance.length === 0 ? <p>No attendance records</p> :
              attendance.map(a => (
                <div key={a._id} style={styles.card}>
                  <p><strong>{a.employee?.name}</strong></p>
                  <p>Date: {a.date}</p>
                  <p>Status: {a.status}</p>
                </div>
              ))
            }
          </>
        )}

        {selected === "appointments" && (
          <>
            <h3>Appointments (Verify)</h3>
            {appointments.length === 0 ? <p>No appointments</p> :
              appointments.map(a => (
                <div key={a._id} style={styles.card}>
                  <p><strong>Employee:</strong> {a.employee?.name}</p>
                  <p><strong>Doctor:</strong> {a.doctor?.name}</p>
                  <p><strong>Date:</strong> {new Date(a.datetime).toLocaleString()}</p>
                  <p><strong>Verified:</strong> {a.verified ? "Yes" : "No"}</p>
                  {!a.verified && <button style={styles.button} onClick={() => verifyAppointment(a._id)}>Verify</button>}
                </div>
              ))
            }
          </>
        )}
      </div>
    </div>
  );
}
