import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

// Hospital background
const bgImage = "https://images.unsplash.com/photo-1588776814546-7bdf0f1d25ee?auto=format&fit=crop&w=1470&q=80";

// --- Global Styles ---
const styles = {
  container: {
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundImage: `url(${bgImage})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    fontFamily: "Arial, sans-serif",
  },
  card: {
    background: "rgba(255, 255, 255, 0.85)",
    padding: "40px",
    borderRadius: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
    width: "350px",
    textAlign: "center",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
  },
  button: {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "8px",
    border: "none",
    fontSize: "16px",
    cursor: "pointer",
  },
  link: {
    color: "#007bff",
    cursor: "pointer",
    marginTop: "10px",
    display: "block",
  },
  dashboardWrapper: {
    minHeight: "100vh",
    padding: "20px",
    backgroundImage: `url(${bgImage})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
  },
  dashboardContent: {
    maxWidth: "900px",
    margin: "0 auto",
    background: "rgba(255,255,255,0.85)",
    padding: "20px",
    borderRadius: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
  },
};

// --- Auth Component ---
function Auth({ onLogin }) {
  const [mode, setMode] = useState("register");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!name || !email || !password || !role) return alert("Fill all fields");
    try {
      await axios.post(API + "/auth/register", { name, email, password, role });
      alert("Registered successfully! Now login.");
      setMode("login");
      setName(""); setEmail(""); setPassword(""); setRole("");
    } catch (err) {
      alert(err.response?.data?.msg || "Register failed");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!email || !password || !role) return alert("Fill all fields");
    try {
      const res = await axios.post(API + "/auth/login", { email, password });
      const u = res.data.user;
      if (u.role !== role) return alert(`Selected role mismatch: ${u.role}`);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(u));
      onLogin(u);
    } catch (err) {
      alert(err.response?.data?.msg || "Login failed");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "20px" }}>
          üè• Hospital Management
        </h2>
        {mode === "register" ? (
          <form onSubmit={handleRegister}>
            <input style={styles.input} type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
            <input style={styles.input} type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input style={styles.input} type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <select style={styles.input} value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button type="submit" style={{ ...styles.button, background: "#28a745", color: "#fff" }}>Register</button>
            <span style={styles.link} onClick={() => setMode("login")}>Already registered? Login</span>
          </form>
        ) : (
          <form onSubmit={handleLogin}>
            <input style={styles.input} type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input style={styles.input} type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <select style={styles.input} value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button type="submit" style={{ ...styles.button, background: "#007bff", color: "#fff" }}>Login</button>
            <span style={styles.link} onClick={() => setMode("register")}>Need to register? Sign up</span>
          </form>
        )}
      </div>
    </div>
  );
}

// --- Dashboard Wrapper ---
function DashboardWrapper({ user, onLogout, children }) {
  return (
    <div style={styles.dashboardWrapper}>
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px", maxWidth: "900px", margin: "0 auto" }}>
        <h1 style={{ fontSize: "24px", fontWeight: "bold" }}>Welcome, {user.name} ({user.role})</h1>
        <button onClick={onLogout} style={{ ...styles.button, background: "#dc3545", color: "#fff", width: "auto", padding: "10px 20px" }}>Logout</button>
      </div>
      <div style={styles.dashboardContent}>{children}</div>
    </div>
  );
}

// --- Employee Dashboard ---
function EmployeeDashboard({ user }) {
  const [appts, setAppts] = useState([]);
  const [date, setDate] = useState("");
  const [reason, setReason] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    const res = await axios.get(API + "/appointments", { headers: { Authorization: "Bearer " + token } });
    setAppts(res.data.filter((a) => a.employee?._id === user._id));
  };

  const bookAppointment = async (e) => {
    e.preventDefault();
    await axios.post(API + "/appointments", { date, reason }, { headers: { Authorization: "Bearer " + token } });
    setDate("");
    setReason("");
    fetchAppointments();
  };

  return (
    <div>
      <h2>Employee Dashboard</h2>
      <form onSubmit={bookAppointment} style={{ marginBottom: "20px" }}>
        <input style={styles.input} type="datetime-local" value={date} onChange={(e) => setDate(e.target.value)} required />
        <input style={styles.input} type="text" placeholder="Reason" value={reason} onChange={(e) => setReason(e.target.value)} />
        <button style={{ ...styles.button, background: "#28a745", color: "#fff" }}>Book Appointment</button>
      </form>
      <h3>Your Appointments</h3>
      {appts.map((a) => (
        <div key={a._id} style={{ border: "1px solid #ccc", padding: "10px", borderRadius: "10px", marginBottom: "10px" }}>
          {new Date(a.date).toLocaleString()} - {a.reason} - Verified: {String(a.verified)}
        </div>
      ))}
    </div>
  );
}

// --- Main App ---
export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = JSON.parse(localStorage.getItem("user"));
    if (token && userData) setUser(userData);
  }, []);

  const handleLogin = (u) => {
    localStorage.setItem("user", JSON.stringify(u));
    setUser(u);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  if (!user) return <Auth onLogin={handleLogin} />;

  return (
    <DashboardWrapper user={user} onLogout={handleLogout}>
      {user.role === "employee" && <EmployeeDashboard user={user} />}
      {/* You can add DoctorDashboard and HRDashboard similarly */}
    </DashboardWrapper>
  );
}
