// src/dashboards/EmployeeDashboard.js
import React, { useState } from "react";

const styles = {
  container: { padding: "20px", fontFamily: "Arial" },
  section: { marginBottom: "20px", background: "#fff", padding: "15px", borderRadius: "10px", boxShadow: "0 2px 5px rgba(0,0,0,0.1)" },
  label: { display: "block", marginBottom: "5px", fontWeight: "bold" },
  input: { padding: "6px", marginBottom: "10px", width: "100%" },
  button: { padding: "8px 12px", background: "#6f42c1", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" },
  logoutBtn: { background: "#dc3545", marginTop: "20px" }
};

export default function EmployeeDashboard({ user, onLogout }) {
  const [status, setStatus] = useState("Present");
  const [doctor, setDoctor] = useState("");
  const [datetime, setDatetime] = useState("");

  const markAttendance = () => {
    const records = JSON.parse(localStorage.getItem("attendance") || "[]");
    records.push({ employeeEmail: user.email, status, date: new Date().toLocaleDateString() });
    localStorage.setItem("attendance", JSON.stringify(records));
    alert("Attendance marked!");
  };

  const bookAppointment = () => {
    const appointments = JSON.parse(localStorage.getItem("appointments") || "[]");
    appointments.push({
      employeeEmail: user.email,
      doctor,
      datetime,
      verified: false
    });
    localStorage.setItem("appointments", JSON.stringify(appointments));
    alert("Appointment booked!");
  };

  return (
    <div style={styles.container}>
      <h2>Welcome, {user.name || user.email}</h2>

      <div style={styles.section}>
        <h3>Mark Attendance</h3>
        <select value={status} onChange={(e) => setStatus(e.target.value)} style={styles.input}>
          <option value="Present">Present</option>
          <option value="Absent">Absent</option>
        </select>
        <button style={styles.button} onClick={markAttendance}>Submit</button>
      </div>

      <div style={styles.section}>
        <h3>Book Appointment</h3>
        <label style={styles.label}>Doctor Name</label>
        <input style={styles.input} value={doctor} onChange={(e) => setDoctor(e.target.value)} />
        <label style={styles.label}>Date & Time</label>
        <input type="datetime-local" style={styles.input} value={datetime} onChange={(e) => setDatetime(e.target.value)} />
        <button style={styles.button} onClick={bookAppointment}>Book</button>
      </div>

      <button style={{ ...styles.button, ...styles.logoutBtn }} onClick={onLogout}>Logout</button>
    </div>
  );
}
