import React, { useState, useEffect } from "react";
import axios from "axios";

const HRDashboard = () => {
  const [employees, setEmployees] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [attendanceForm, setAttendanceForm] = useState({});
  const [selected, setSelected] = useState("attendance");

  useEffect(() => {
    fetchEmployees();
    fetchAttendance();
  }, []);

  const fetchEmployees = async () => {
    try {
      const res = await axios.get("/api/hr/employees", {
        headers: { "x-auth-token": localStorage.getItem("token") },
      });
      setEmployees(res.data);
    } catch (err) {
      console.error("Error fetching employees:", err);
    }
  };

  const fetchAttendance = async () => {
    try {
      const res = await axios.get("/api/hr/attendance", {
        headers: { "x-auth-token": localStorage.getItem("token") },
      });
      setAttendance(res.data);
    } catch (err) {
      console.error("Error fetching attendance:", err);
    }
  };

  const markAttendance = (id, status) => {
    setAttendanceForm((prev) => ({ ...prev, [id]: status }));
  };

  const submitAttendance = async () => {
    try {
      const attendanceData = Object.keys(attendanceForm).map((id) => ({
        employeeId: id,
        status: attendanceForm[id],
      }));

      await axios.post(
        "/api/hr/attendance",
        { attendance: attendanceData },
        { headers: { "x-auth-token": localStorage.getItem("token") } }
      );

      alert("Attendance submitted successfully!");
      fetchAttendance();
      setAttendanceForm({});
    } catch (err) {
      console.error("Error submitting attendance:", err);
      alert("Failed to submit attendance");
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>HR Dashboard</h2>

      <div style={styles.sidebar}>
        <button
          style={{
            ...styles.tab,
            background: selected === "attendance" ? "#007bff" : "#f0f0f0",
            color: selected === "attendance" ? "#fff" : "#000",
          }}
          onClick={() => setSelected("attendance")}
        >
          üìã Attendance
        </button>

        <button
          style={{
            ...styles.tab,
            background: selected === "employees" ? "#007bff" : "#f0f0f0",
            color: selected === "employees" ? "#fff" : "#000",
          }}
          onClick={() => setSelected("employees")}
        >
          üë• Employees
        </button>
      </div>

      <div style={styles.content}>
        {selected === "attendance" && (
          <div>
            <h3>Mark Attendance</h3>
            <div style={styles.total}>Total Staff: {employees.length}</div>

            {employees.map((emp) => (
              <div key={emp._id} style={styles.card}>
                <div>
                  <p><strong>Name:</strong> {emp.name}</p>
                  <p><strong>Email:</strong> {emp.email}</p>
                  <p><strong>Role:</strong> {emp.role}</p>
                </div>
                <div>
                  <button
                    style={{
                      ...styles.button,
                      background:
                        attendanceForm[emp._id] === "present"
                          ? "#28a745"
                          : "#e0e0e0",
                      color:
                        attendanceForm[emp._id] === "present"
                          ? "#fff"
                          : "#000",
                    }}
                    onClick={() => markAttendance(emp._id, "present")}
                  >
                    ‚úÖ Present
                  </button>
                  <button
                    style={{
                      ...styles.button,
                      background:
                        attendanceForm[emp._id] === "absent"
                          ? "#dc3545"
                          : "#e0e0e0",
                      color:
                        attendanceForm[emp._id] === "absent"
                          ? "#fff"
                          : "#000",
                    }}
                    onClick={() => markAttendance(emp._id, "absent")}
                  >
                    ‚ùå Absent
                  </button>
                </div>
              </div>
            ))}

            <button style={styles.submitBtn} onClick={submitAttendance}>
              Submit Attendance
            </button>

            <hr style={{ margin: "20px 0" }} />

            <h3>Attendance Records</h3>
            {attendance.length === 0 ? (
              <p>No records yet</p>
            ) : (
              attendance.map((a) => (
                <div key={a._id} style={styles.card}>
                  <p><strong>Date:</strong> {a.date}</p>
                  <p><strong>Name:</strong> {a.employeeId?.name}</p>
                  <p><strong>Status:</strong> {a.status}</p>
                </div>
              ))
            )}
          </div>
        )}

        {selected === "employees" && (
          <div>
            <h3>All Staff</h3>
            <div style={styles.total}>Total Staff: {employees.length}</div>
            {employees.map((emp) => (
              <div key={emp._id} style={styles.card}>
                <p><strong>Name:</strong> {emp.name}</p>
                <p><strong>Email:</strong> {emp.email}</p>
                <p><strong>Role:</strong> {emp.role}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default HRDashboard;

const styles = {
  container: { display: "flex", padding: "20px", fontFamily: "Arial" },
  header: { position: "absolute", top: "10px", left: "20px" },
  sidebar: { width: "200px", marginRight: "20px", display: "flex", flexDirection: "column", gap: "10px" },
  tab: { padding: "10px", border: "none", borderRadius: "5px", cursor: "pointer" },
  content: { flex: 1 },
  total: { fontWeight: "bold", marginBottom: "10px" },
  card: {
    background: "#f9f9f9",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  button: {
    border: "none",
    padding: "8px 12px",
    borderRadius: "5px",
    marginLeft: "5px",
    cursor: "pointer",
  },
  submitBtn: {
    marginTop: "15px",
    padding: "10px 15px",
    background: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};
