import React, { useEffect, useState } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

function HRDashboard({ user, onLogout }) {
  const [employees, setEmployees] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token"); // ya jaha aap token rakhte ho

    const fetchData = async () => {
      try {
        const [empRes, docRes, appRes] = await Promise.all([
          axios.get(`${API}/hr/employees`, { headers: { Authorization: token } }),
          axios.get(`${API}/hr/doctors`, { headers: { Authorization: token } }),
          axios.get(`${API}/hr/appointments`, { headers: { Authorization: token } }),
        ]);

        setEmployees(empRes.data);
        setDoctors(docRes.data);
        setAppointments(appRes.data);
      } catch (err) {
        console.error("Error fetching HR data:", err);
      }
    };

    fetchData();
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>HR Dashboard</h1>
      <button onClick={onLogout} style={{ marginBottom: "20px" }}>Logout</button>

      {/* üë®‚Äçüíº Employees Section */}
      <section>
        <h2>All Employees</h2>
        <table border="1" cellPadding="6" width="100%">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Department</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((emp) => (
              <tr key={emp._id}>
                <td>{emp.name}</td>
                <td>{emp.email}</td>
                <td>{emp.department || "N/A"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      <br />

      {/* üë®‚Äç‚öïÔ∏è Doctors Section */}
      <section>
        <h2>All Doctors</h2>
        <table border="1" cellPadding="6" width="100%">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Specialization</th>
            </tr>
          </thead>
          <tbody>
            {doctors.map((doc) => (
              <tr key={doc._id}>
                <td>{doc.name}</td>
                <td>{doc.email}</td>
                <td>{doc.specialization || "N/A"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      <br />

      {/* üìù Appointments Section */}
      <section>
        <h2>All Appointments</h2>
        <table border="1" cellPadding="6" width="100%">
          <thead>
            <tr>
              <th>Employee</th>
              <th>Doctor</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((app) => (
              <tr key={app._id}>
                <td>{app.employeeId?.name}</td>
                <td>{app.doctorId?.name}</td>
                <td>{new Date(app.date).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

export default HRDashboard;
