import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "220px",
    background: "#007bff",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
  },
  sidebarItem: (active) => ({
    padding: "15px 20px",
    cursor: "pointer",
    background: active ? "#0056b3" : "transparent",
    margin: "5px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  input: { padding: "10px", width: "70%", marginBottom: "10px", borderRadius: "5px" },
  select: { padding: "10px", width: "74%", marginBottom: "10px", borderRadius: "5px" },
  button: { padding: "10px 15px", borderRadius: "5px", cursor: "pointer", background: "#007bff", color: "#fff", border: "none" },
  logoutBtn: { padding: "8px 12px", marginTop: "20px", borderRadius: "5px", cursor: "pointer", background: "#dc3545", color: "#fff", border: "none" },
};

export default function EmployeeDashboard({ user, logout }) {
  const [selected, setSelected] = useState("attendance");
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [medicines, setMedicines] = useState([]);
  const [doctors, setDoctors] = useState([]);
  
  // Appointment fields
  const [doctorId, setDoctorId] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");

  useEffect(() => {
    fetchAttendance();
    fetchAppointments();
    fetchMedicines();
    fetchDoctors();
  }, []);

  // Fetch Attendance
  const fetchAttendance = async () => {
    try {
      const res = await axios.get(`${API}/employee/attendance`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAttendance(res.data);
    } catch (err) { console.error(err); }
  };

  // Fetch Appointments
  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/employee/appointments`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAppointments(res.data);
    } catch (err) { console.error(err); }
  };

  // Fetch Medicines
  const fetchMedicines = async () => {
    try {
      const res = await axios.get(`${API}/medicines`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setMedicines(res.data);
    } catch (err) { console.error(err); }
  };

  // Fetch Doctors
  const fetchDoctors = async () => {
    try {
      const res = await axios.get(`${API}/doctors`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setDoctors(res.data);
    } catch (err) { console.error(err); }
  };

  // Book Appointment
  const bookAppointment = async () => {
    if (!doctorId || !appointmentDate || !appointmentTime) return alert("Fill all fields");
    try {
      const res = await axios.post(
        `${API}/employee/appointments`,
        {
          name: user.name,
          email: user.email,
          doctorId,
          date: appointmentDate,
          time: appointmentTime,
        },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      setAppointments((prev) => [...prev, res.data]);
      // Clear form
      setDoctorId("");
      setAppointmentDate("");
      setAppointmentTime("");
      alert("Appointment booked successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to book appointment");
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{ textAlign: "center" }}>Employee</h2>
        <div style={styles.sidebarItem(selected === "attendance")} onClick={() => setSelected("attendance")}>Attendance</div>
        <div style={styles.sidebarItem(selected === "appointments")} onClick={() => setSelected("appointments")}>Appointments</div>
        <div style={styles.sidebarItem(selected === "medicines")} onClick={() => setSelected("medicines")}>Medicines</div>
        <button style={styles.logoutBtn} onClick={logout}>Logout</button>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {selected === "attendance" && (
          <>
            <h3>Attendance</h3>
            {attendance.length === 0 ? <p>No attendance records</p> : attendance.map((a) => (
              <div key={a._id} style={styles.card}>Date: {new Date(a.date).toLocaleDateString()} | Status: {a.status}</div>
            ))}
          </>
        )}

        {selected === "appointments" && (
          <>
            <h3>Appointments</h3>
            {appointments.length === 0 ? <p>No appointments</p> : appointments.map((a) => (
              <div key={a._id} style={styles.card}>
                {a.name} | {a.email} | Doctor: {a.doctorName} | Date: {a.date} | Time: {a.time}
              </div>
            ))}

            <h4>Book New Appointment</h4>
            <div>
              <label>Doctor:</label>
              <select style={styles.select} value={doctorId} onChange={(e) => setDoctorId(e.target.value)}>
                <option value="">-- Select Doctor --</option>
                {doctors.map((d) => (
                  <option key={d._id} value={d._id}>{d.name}</option>
                ))}
              </select>
            </div>
            <div>
              <input type="date" style={styles.input} value={appointmentDate} onChange={(e) => setAppointmentDate(e.target.value)} />
            </div>
            <div>
              <input type="time" style={styles.input} value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} />
            </div>
            <button style={styles.button} onClick={bookAppointment}>Book Appointment</button>
          </>
        )}

        {selected === "medicines" && (
          <>
            <h3>Medicines</h3>
            {medicines.length === 0 ? <p>No medicines</p> : medicines.map((m) => (
              <div key={m._id} style={styles.card}>{m.name} | Quantity: {m.quantity}</div>
            ))}
          </>
        )}
      </div>
    </div>
  );
}
