// --- Auth Component ---
function Auth({ onLogin }) {
  const [mode, setMode] = useState("register");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!name || !email || !password || !role) return alert("Fill all fields");
    try {
      await axios.post(API + "/auth/register", { name, email, password, role });
      alert("Registered successfully! Now login.");
      setMode("login");
      setName(""); setEmail(""); setPassword(""); setRole("");
    } catch (err) {
      alert(err.response?.data?.msg || "Register failed");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!email || !password || !role) return alert("Fill all fields");
    try {
      const res = await axios.post(API + "/auth/login", { email, password });
      const u = res.data.user;
      if (u.role !== role) return alert(`Selected role mismatch: ${u.role}`);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(u));
      onLogin(u);
    } catch (err) {
      alert(err.response?.data?.msg || "Login failed");
    }
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "20px" }}>
          üè• Hospital Management
        </h2>
        {mode === "register" ? (
          <form onSubmit={handleRegister}>
            <input style={styles.input} type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
            <input style={styles.input} type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input style={styles.input} type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <select style={styles.input} value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button type="submit" style={{ ...styles.button, background: "#28a745", color: "#fff" }}>Register</button>
            <span style={styles.link} onClick={() => setMode("login")}>Already registered? Login</span>
          </form>
        ) : (
          <form onSubmit={handleLogin}>
            <input style={styles.input} type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input style={styles.input} type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <select style={styles.input} value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button type="submit" style={{ ...styles.button, background: "#007bff", color: "#fff" }}>Login</button>
            <span style={styles.link} onClick={() => setMode("register")}>Need to register? Sign up</span>
          </form>
        )}
      </div>
    </div>
  );
}

