// --- HR Dashboard ---
function HRDashboard({ user }) {
  const [appts, setAppts] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => { fetchAppointments(); }, []);

  const fetchAppointments = async () => {
    const res = await axios.get(API + "/appointments", { headers: { Authorization: "Bearer " + token } });
    setAppts(res.data);
  };

  const verifyAppointment = async (id) => {
    await axios.put(API + "/appointments/" + id + "/verify", {}, { headers: { Authorization: "Bearer " + token } });
    fetchAppointments();
  };

  return (
    <div>
      <h2>HR Dashboard</h2>
      {appts.map((a) => (
        <div key={a._id} style={styles.appointmentCard}>
          {new Date(a.date).toLocaleString()} - Employee: {a.employee?.name} - Verified: {String(a.verified)}
          {!a.verified && <button onClick={() => verifyAppointment(a._id)} style={{ ...styles.button, background: "#007bff", color: "#fff", width: "auto", marginTop: "5px" }}>Verify</button>}
        </div>
      ))}
    </div>
  );
}

// --- Main App ---
export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = JSON.parse(localStorage.getItem("user"));
    if (token && userData) setUser(userData);
  }, []);

  const handleLogin = (u) => { localStorage.setItem("user", JSON.stringify(u)); setUser(u); };
  const handleLogout = () => { localStorage.removeItem("token"); localStorage.removeItem("user"); setUser(null); };

  if (!user) return <Auth onLogin={handleLogin} />;

  switch (user.role) {
    case "employee":
      return <DashboardWrapper user={user} onLogout={handleLogout}><EmployeeDashboard user={user} /></DashboardWrapper>;
    case "doctor":
      return <DashboardWrapper user={user} onLogout={handleLogout}><DoctorDashboard user={user} /></DashboardWrapper>;
    case "hr":
      return <DashboardWrapper user={user} onLogout={handleLogout}><HRDashboard user={user} /></DashboardWrapper>;
    default:
      return <div>Unknown role</div>;
  }
}
