import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "220px",
    background: "#007bff",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
  },
  sidebarItem: (active) => ({
    padding: "15px 20px",
    cursor: "pointer",
    background: active ? "#0056b3" : "transparent",
    margin: "5px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  input: { padding: "10px", width: "70%", marginRight: "10px", borderRadius: "5px" },
  button: { padding: "10px 15px", borderRadius: "5px", cursor: "pointer", background: "#007bff", color: "#fff", border: "none" },
  logoutBtn: { padding: "8px 12px", marginTop: "20px", borderRadius: "5px", cursor: "pointer", background: "#dc3545", color: "#fff", border: "none" },
};

export default function EmployeeDashboard({ user, logout }) {
  const [selected, setSelected] = useState("attendance"); // attendance / appointments / medicines
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [medicines, setMedicines] = useState([]);
  const [newAppointment, setNewAppointment] = useState("");

  useEffect(() => {
    fetchAttendance();
    fetchAppointments();
    fetchMedicines();
  }, []);

  const fetchAttendance = async () => {
    try {
      const res = await axios.get(`${API}/employee/attendance`, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setAttendance(res.data);
    } catch (err) { console.error(err); }
  };

  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/employee/appointments`, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setAppointments(res.data);
    } catch (err) { console.error(err); }
  };

  const fetchMedicines = async () => {
    try {
      const res = await axios.get(`${API}/medicines`, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setMedicines(res.data);
    } catch (err) { console.error(err); }
  };

  const bookAppointment = async () => {
    if (!newAppointment) return alert("Enter appointment details");
    try {
      const res = await axios.post(`${API}/employee/appointments`, { details: newAppointment }, { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
      setAppointments([...appointments, res.data]);
      setNewAppointment("");
    } catch (err) {
      console.error(err);
      alert("Failed to book appointment");
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{textAlign:"center"}}>Employee</h2>
        <div style={styles.sidebarItem(selected === "attendance")} onClick={() => setSelected("attendance")}>Attendance</div>
        <div style={styles.sidebarItem(selected === "appointments")} onClick={() => setSelected("appointments")}>Appointments</div>
        <div style={styles.sidebarItem(selected === "medicines")} onClick={() => setSelected("medicines")}>Medicines</div>
        <button style={styles.logoutBtn} onClick={logout}>Logout</button>
      </div>

      {/* Content Area */}
      <div style={styles.content}>
        {selected === "attendance" && (
          <>
            <h3>Attendance</h3>
            {attendance.length === 0 ? <p>No attendance records</p> : attendance.map(a => (
              <div key={a._id} style={styles.card}>Date: {a.date} | Status: {a.status}</div>
            ))}
          </>
        )}

        {selected === "appointments" && (
          <>
            <h3>Appointments</h3>
            {appointments.length === 0 ? <p>No appointments</p> : appointments.map(a => (
              <div key={a._id} style={styles.card}>{a.details} | Date: {a.date}</div>
            ))}
            <div style={{marginTop:"10px"}}>
              <input style={styles.input} placeholder="New Appointment" value={newAppointment} onChange={(e)=>setNewAppointment(e.target.value)} />
              <button style={styles.button} onClick={bookAppointment}>Book</button>
            </div>
          </>
        )}

        {selected === "medicines" && (
          <>
            <h3>Medicines</h3>
            {medicines.length === 0 ? <p>No medicines</p> : medicines.map(m => (
              <div key={m._id} style={styles.card}>{m.name} | Quantity: {m.quantity}</div>
            ))}
          </>
        )}
      </div>
    </div>
  );
}
