import React, { useState, useEffect } from "react";
import axios from "axios";
import bgHospital from "./297e9424-5dd9-4bfb-a0af-871575c90f3f.png"; // uploaded image

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

// --- Auth Component (Register/Login) ---
function Auth({ onLogin }) {
  const [mode, setMode] = useState("register");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!name || !email || !password || !role) return alert("Fill all fields");
    try {
      await axios.post(API + "/auth/register", { name, email, password, role });
      alert("Registered successfully! Now login.");
      setMode("login");
      setName(""); setEmail(""); setPassword(""); setRole("");
    } catch (err) {
      alert(err.response?.data?.msg || "Register failed");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!email || !password || !role) return alert("Fill all fields");
    try {
      const res = await axios.post(API + "/auth/login", { email, password });
      const u = res.data.user;
      if (u.role !== role) return alert(`Selected role mismatch: ${u.role}`);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(u));
      onLogin(u);
    } catch (err) {
      alert(err.response?.data?.msg || "Login failed");
    }
  };

  return (
    <div
      className="min-h-screen flex items-center justify-center bg-cover bg-center"
      style={{ backgroundImage: `url(${bgHospital})` }}
    >
      <div className="bg-white p-10 rounded-3xl shadow-2xl w-full max-w-md space-y-6 backdrop-blur-sm bg-opacity-80">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-4">
          üè• Hospital Management
        </h2>

        {mode === "register" ? (
          <form onSubmit={handleRegister} className="space-y-4">
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            >
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button
              type="submit"
              className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition-colors"
            >
              Register
            </button>
            <p
              className="text-center text-blue-600 cursor-pointer hover:underline"
              onClick={() => setMode("login")}
            >
              Already registered? Login
            </p>
          </form>
        ) : (
          <form onSubmit={handleLogin} className="space-y-4">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            >
              <option value="">-- Select Role --</option>
              <option value="employee">Employee</option>
              <option value="doctor">Doctor</option>
              <option value="hr">HR</option>
            </select>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors"
            >
              Login
            </button>
            <p
              className="text-center text-green-600 cursor-pointer hover:underline"
              onClick={() => setMode("register")}
            >
              Need to register? Sign up
            </p>
          </form>
        )}
      </div>
    </div>
  );
}

// --- Dashboard Wrapper with background ---
function DashboardWrapper({ user, onLogout, children }) {
  return (
    <div
      className="min-h-screen p-6 bg-cover bg-center"
      style={{ backgroundImage: `url(${bgHospital})` }}
    >
      <div className="flex justify-between items-center mb-6 max-w-6xl mx-auto bg-white bg-opacity-60 p-4 rounded-xl shadow">
        <h1 className="text-3xl font-bold text-gray-800">
          üè• Welcome, {user.name} ({user.role})
        </h1>
        <button
          onClick={onLogout}
          className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
        >
          Logout
        </button>
      </div>
      <div className="max-w-6xl mx-auto">{children}</div>
    </div>
  );
}

// (Employee, Doctor, HR dashboards remain same as previous code)

// --- Main App ---
export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = JSON.parse(localStorage.getItem("user"));
    if (token && userData) setUser(userData);
  }, []);

  const handleLogin = (u) => {
    localStorage.setItem("user", JSON.stringify(u));
    setUser(u);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  if (!user) return <Auth onLogin={handleLogin} />;

  switch (user.role) {
    case "employee":
      return (
        <DashboardWrapper user={user} onLogout={handleLogout}>
          <EmployeeDashboard user={user} />
        </DashboardWrapper>
      );
    case "doctor":
      return (
        <DashboardWrapper user={user} onLogout={handleLogout}>
          <DoctorDashboard user={user} />
        </DashboardWrapper>
      );
    case "hr":
      return (
        <DashboardWrapper user={user} onLogout={handleLogout}>
          <HRDashboard user={user} />
        </DashboardWrapper>
      );
    default:
      return <div>Unknown role</div>;
  }
}
