import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "200px",
    background: "#6f42c1",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
    overflowY: "auto",
  },
  sidebarItem: (active) => ({
    padding: "10px 20px",
    cursor: "pointer",
    background: active ? "#593196" : "transparent",
    margin: "2px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  summaryCard: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  total: { fontSize: "18px", fontWeight: "bold", marginBottom: "10px" },
  button: { padding: "6px 12px", borderRadius: "5px", cursor: "pointer", marginLeft: "10px", border: "none" },
  logoutBtn: { padding: "8px 12px", marginTop: "20px", borderRadius: "5px", cursor: "pointer", background: "#dc3545", color: "#fff", border: "none" },
};

export default function HRDashboard({ onLogout }) {
  const [selectedCategory, setSelectedCategory] = useState("employees");
  const [selectedItemId, setSelectedItemId] = useState(null);

  const [employees, setEmployees] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    await Promise.all([fetchEmployees(), fetchDoctors(), fetchAttendance(), fetchAppointments()]);
  };

  const fetchEmployees = async () => {
    try {
      const res = await axios.get(`${API}/hr/employees`, { headers: { Authorization: `Bearer ${token}` } });
      setEmployees(res.data);
    } catch (err) {
      console.error("Employees fetch failed", err);
    }
  };

  const fetchDoctors = async () => {
    try {
      const res = await axios.get(`${API}/hr/doctors`, { headers: { Authorization: `Bearer ${token}` } });
      setDoctors(res.data);
    } catch (err) {
      console.error("Doctors fetch failed", err);
    }
  };

  const fetchAttendance = async () => {
    try {
      const res = await axios.get(`${API}/hr/attendance`, { headers: { Authorization: `Bearer ${token}` } });
      setAttendance(res.data);
    } catch (err) {
      console.error("Attendance fetch failed", err);
    }
  };

  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/hr/appointments`, { headers: { Authorization: `Bearer ${token}` } });
      setAppointments(res.data);
    } catch (err) {
      console.error("Appointments fetch failed", err);
    }
  };

  const verifyAppointment = async (id) => {
    try {
      const res = await axios.put(`${API}/hr/appointments/${id}/verify`, {}, { headers: { Authorization: `Bearer ${token}` } });
      setAppointments(appointments.map(a => a._id === id ? res.data : a));
    } catch (err) {
      console.error("Verify failed", err);
    }
  };

  // Determine selected data
  let selectedData = [];
  if (selectedCategory === "employees") selectedData = employees;
  else if (selectedCategory === "doctors") selectedData = doctors;

  // Selected item details
  const selectedItem = selectedData.find(item => item._id === selectedItemId);

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{ textAlign: "center" }}>HR Panel</h2>

        <div style={styles.sidebarItem(selectedCategory === "employees")} onClick={() => { setSelectedCategory("employees"); setSelectedItemId(null); }}>
          Employees
        </div>
        {selectedCategory === "employees" && employees.map(emp => (
          <div key={emp._id} style={styles.sidebarItem(selectedItemId === emp._id)} onClick={() => setSelectedItemId(emp._id)}>
            {emp.name}
          </div>
        ))}

        <div style={styles.sidebarItem(selectedCategory === "doctors")} onClick={() => { setSelectedCategory("doctors"); setSelectedItemId(null); }}>
          Doctors
        </div>
        {selectedCategory === "doctors" && doctors.map(doc => (
          <div key={doc._id} style={styles.sidebarItem(selectedItemId === doc._id)} onClick={() => setSelectedItemId(doc._id)}>
            {doc.name}
          </div>
        ))}

        <button style={styles.logoutBtn} onClick={onLogout}>Logout</button>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {/* Summary */}
        <div style={styles.summaryCard}>
          <p style={styles.total}>Total Employees: {employees.length}</p>
          <p>Total Doctors: {doctors.length}</p>
          <p>Total Attendance Records: {attendance.length}</p>
          <p>Total Appointments: {appointments.length}</p>
          <p>Verified Appointments: {appointments.filter(a => a.verified).length}</p>
        </div>

        {/* Selected employee/doctor details */}
        {selectedItem && (
          <div style={styles.card}>
            <p><strong>Name:</strong> {selectedItem.name}</p>
            <p><strong>Email:</strong> {selectedItem.email}</p>
            <p><strong>Role:</strong> {selectedItem.role}</p>
          </div>
        )}

        {/* Attendance List */}
        <h3>Attendance Records</h3>
        {attendance.map(a => (
          <div key={a._id} style={styles.card}>
            <p><strong>Employee:</strong> {a.employeeId?.name || "Unknown"}</p>
            <p><strong>Date:</strong> {new Date(a.date).toLocaleDateString()}</p>
            <p><strong>Status:</strong> {a.status}</p>
          </div>
        ))}

        {/* Appointments List */}
        <h3>Appointments</h3>
        {appointments.map(a => (
          <div key={a._id} style={styles.card}>
            <p><strong>Employee:</strong> {a.employeeId?.name || "Unknown"}</p>
            <p><strong>Doctor:</strong> {a.doctorId?.name || "Unknown"}</p>
            <p><strong>Date & Time:</strong> {new Date(a.datetime).toLocaleString()}</p>
            <p><strong>Verified:</strong> {a.verified ? "Yes" : "No"}</p>
            {!a.verified && (
              <button style={{ ...styles.button, background: "#28a745", color: "#fff" }} onClick={() => verifyAppointment(a._id)}>
                Verify
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
