import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

// --- Register & Login Component ---
function Auth({ onLogin }) {
  const [mode, setMode] = useState("register"); // "register" or "login"
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!name || !email || !password || !role) return alert("Fill all fields");
    try {
      await axios.post(API + "/auth/register", { name, email, password, role });
      alert("Registered successfully! Now login.");
      setMode("login");
      setName(""); setEmail(""); setPassword(""); setRole("");
    } catch (err) {
      alert(err.response?.data?.msg || "Register failed");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!email || !password || !role) return alert("Fill all fields");
    try {
      const res = await axios.post(API + "/auth/login", { email, password });
      const u = res.data.user;
      if (u.role !== role) return alert(`Selected role mismatch: ${u.role}`);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(u));
      onLogin(u);
    } catch (err) {
      alert(err.response?.data?.msg || "Login failed");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-blue-200 p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md space-y-4">
        <h2 className="text-2xl font-bold text-center text-gray-800">
          üè• Hospital Management
        </h2>

        {mode === "register" ? (
          <form onSubmit={handleRegister} className="space-y-4">
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full border rounded px-3 py-2"
            />
