// --- Employee Dashboard ---
function EmployeeDashboard({ user }) {
  const [appts, setAppts] = useState([]);
  const [date, setDate] = useState("");
  const [reason, setReason] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    const res = await axios.get(API + "/appointments", {
      headers: { Authorization: "Bearer " + token },
    });
    setAppts(res.data.filter((a) => a.employee?._id === user._id));
  };

  const bookAppointment = async (e) => {
    e.preventDefault();
    await axios.post(
      API + "/appointments",
      { date, reason },
      { headers: { Authorization: "Bearer " + token } }
    );
    setDate("");
    setReason("");
    fetchAppointments();
  };

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Employee Dashboard</h2>

      <form onSubmit={bookAppointment} className="mb-6 space-y-2">
        <input
          type="datetime-local"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border rounded px-3 py-2 w-full"
          required
        />
        <input
          type="text"
          placeholder="Reason"
          value={reason}
          onChange={(e) => setReason(e.target.value)}
          className="border rounded px-3 py-2 w-full"
        />
        <button className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
          Book Appointment
        </button>
      </form>

      <h3 className="text-xl font-semibold mb-2">Your Appointments</h3>
      <ul className="space-y-1">
        {appts.map((a) => (
          <li
            key={a._id}
            className="border p-2 rounded flex justify-between items-center"
          >
            <span>
              {new Date(a.date).toLocaleString()} - {a.reason} - Verified:{" "}
              {String(a.verified)}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
}

