import React, {useState, useEffect} from 'react';
import axios from 'axios';

const API = process.env.REACT_APP_API || 'http://localhost:5000/api';

function Login({onLogin}) {
  const [email,setEmail]=useState(''); const [password,setPassword]=useState('');
  const handle = async e => {
    e.preventDefault();
    try{
      const res = await axios.post(API + '/auth/login', {email,password});
      localStorage.setItem('token', res.data.token);
      onLogin(res.data.user);
    }catch(err){ alert(err.response?.data?.msg || 'Login failed'); }
  };
  return <form onSubmit={handle} className="card">
    <h3>Login</h3>
    <input placeholder="email" value={email} onChange={e=>setEmail(e.target.value)} />
    <input placeholder="password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
    <button>Login</button>
  </form>;
}

function EmployeeDashboard({user}) {
  const [appts,setAppts]=useState([]); const [date,setDate]=useState(''); const [reason,setReason]=useState('');
  const token = localStorage.getItem('token');
  useEffect(()=>{ fetch(); },[]);
  const fetch = async ()=> {
    const res = await axios.get(API + '/appointments', {headers:{Authorization:'Bearer '+token}});
    setAppts(res.data);
  };
  const book = async e => {
    e.preventDefault();
    await axios.post(API + '/appointments', {date,reason}, {headers:{Authorization:'Bearer '+token}});
    setDate(''); setReason(''); fetch();
  };
  return <div className="card">
    <h3>Employee Dashboard</h3>
    <form onSubmit={book}>
      <input type="datetime-local" value={date} onChange={e=>setDate(e.target.value)} required />
      <input placeholder="reason" value={reason} onChange={e=>setReason(e.target.value)} />
      <button>Book Appointment</button>
    </form>
    <h4>Your Appointments</h4>
    <ul>{appts.map(a=> <li key={a._id}>{new Date(a.date).toLocaleString()} - {a.reason} - Verified: {String(a.verified)}</li>)}</ul>
  </div>;
}

function DoctorDashboard({user}) {
  const [appts,setAppts]=useState([]); const token = localStorage.getItem('token');
  useEffect(()=>{ fetch(); },[]);
  const fetch = async ()=> {
    const res = await axios.get(API + '/appointments', {headers:{Authorization:'Bearer '+token}});
    setAppts(res.data);
  };
  return <div className="card">
    <h3>Doctor Dashboard</h3>
    <h4>Appointments</h4>
    <ul>{appts.map(a=> <li key={a._id}>{new Date(a.date).toLocaleString()} - Employee: {a.employee?.name || a.employee} - {a.reason}</li>)}</ul>
  </div>;
}

function HRDashboard({user}) {
  const [appts,setAppts]=useState([]); const token = localStorage.getItem('token');
  useEffect(()=>{ fetch(); },[]);
  const fetch = async ()=> {
    const res = await axios.get(API + '/appointments', {headers:{Authorization:'Bearer '+token}});
    setAppts(res.data);
  };
  const verify = async (id)=> {
    await axios.put(API + '/appointments/'+id+'/verify', {}, {headers:{Authorization:'Bearer '+token}});
    fetch();
  };
  return <div className="card">
    <h3>HR Dashboard</h3>
    <ul>{appts.map(a=> <li key={a._id}>{new Date(a.date).toLocaleString()} - Employee: {a.employee?.name} - Verified: {String(a.verified)} <button onClick={()=>verify(a._id)}>Verify</button></li>)}</ul>
  </div>;
}

export default function App(){
  const [user,setUser]=useState(null);
  useEffect(()=>{
    const tok = localStorage.getItem('token');
    if(tok){
      // try to get user by calling a protected endpoint (simple approach)
      // We don't have /me, so rely on manual login persistence for demo.
    }
  },[]);
  if(!user) return <div className="centered"><Login onLogin={setUser} /><p className="note">For demo: register users via backend /api/auth/register (role: doctor/employee/hr)</p></div>;
  if(user.role==='employee') return <EmployeeDashboard user={user} />;
  if(user.role==='doctor') return <DoctorDashboard user={user} />;
  if(user.role==='hr') return <HRDashboard user={user} />;
  return <div>Unknown role</div>;
}
