// src/Auth.js
import React, { useState } from "react";
import "./Auth.css";

const Auth = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ email: "", password: "", role: "employee", name: "" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isLogin) {
      // Login: localStorage me users se match karo
      const users = JSON.parse(localStorage.getItem("users") || "[]");
      const found = users.find(
        (u) => u.email === form.email && u.password === form.password && u.role === form.role
      );
      if (found) {
        onLogin(found);
      } else {
        alert("Invalid credentials!");
      }
    } else {
      // Register: naya user add karo localStorage me
      const users = JSON.parse(localStorage.getItem("users") || "[]");
      const newUser = { ...form };
      users.push(newUser);
      localStorage.setItem("users", JSON.stringify(users));
      alert("Registration successful!");
      setIsLogin(true);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h2>{isLogin ? "Login" : "Register"}</h2>
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={form.name}
              onChange={handleChange}
              required
            />
          )}

          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            required
          />

          <select name="role" value={form.role} onChange={handleChange}>
            <option value="employee">Employee</option>
            <option value="doctor">Doctor</option>
            <option value="hr">HR</option>
          </select>

          <button type="submit">{isLogin ? "Login" : "Register"}</button>
        </form>

        <p onClick={() => setIsLogin(!isLogin)} className="toggle-link">
          {isLogin ? "New user? Register here" : "Already have an account? Login"}
        </p>
      </div>
    </div>
  );
};

export default Auth;
