import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

const styles = {
  container: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: {
    width: "220px",
    background: "#6f42c1",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px 0",
  },
  sidebarItem: (active) => ({
    padding: "15px 20px",
    cursor: "pointer",
    background: active ? "#593196" : "transparent",
    margin: "5px 0",
    borderRadius: "5px",
  }),
  content: { flex: 1, padding: "20px", background: "#f0f2f5", overflowY: "auto" },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  total: { fontSize: "18px", fontWeight: "bold", marginBottom: "15px" },
  button: {
    padding: "8px 14px",
    borderRadius: "5px",
    cursor: "pointer",
    border: "none",
    marginRight: "10px",
  },
  submitBtn: {
    padding: "10px 20px",
    background: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
  logoutBtn: {
    padding: "8px 12px",
    marginTop: "20px",
    borderRadius: "5px",
    cursor: "pointer",
    background: "#dc3545",
    color: "#fff",
    border: "none",
  },
};

export default function HRDashboard({ user, onLogout }) {
  const [selected, setSelected] = useState("employees");
  const [employees, setEmployees] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [attendanceForm, setAttendanceForm] = useState({}); // store HR selections

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchEmployees();
    fetchDoctors();
    fetchAttendance();
    fetchAppointments();
  }, []);

  // --- Fetch Data ---
  const fetchEmployees = async () => {
    try {
      const res = await axios.get(`${API}/hr/employees`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEmployees(res.data);
    } catch (err) {
      console.error("Employees fetch failed", err);
    }
  };

  const fetchDoctors = async () => {
    try {
      const res = await axios.get(`${API}/hr/doctors`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDoctors(res.data);
    } catch (err) {
      console.error("Doctors fetch failed", err);
    }
  };

  const fetchAttendance = async () => {
    try {
      const res = await axios.get(`${API}/hr/attendance`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAttendance(res.data);
    } catch (err) {
      console.error("Attendance fetch failed", err);
    }
  };

  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/hr/appointments`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAppointments(res.data);
    } catch (err) {
      console.error("Appointments fetch failed", err);
    }
  };

  // --- Verify Appointment ---
  const verifyAppointment = async (id) => {
    try {
      const res = await axios.put(
        `${API}/hr/appointments/${id}/verify`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments(appointments.map(a => a._id === id ? res.data : a));
    } catch (err) {
      console.error("Verify failed", err);
    }
  };

  // --- Handle Attendance Mark ---
  const markAttendance = (empId, status) => {
    setAttendanceForm(prev => ({ ...prev, [empId]: status }));
  };

  // --- Submit Attendance ---
  const submitAttendance = async () => {
    const records = Object.keys(attendanceForm).map(id => ({
      employeeId: id,
      status: attendanceForm[id],
    }));

    if (records.length === 0) {
      alert("Please mark at least one attendance before submitting!");
      return;
    }

    try {
      await axios.post(
        `${API}/hr/attendance`,
        { records },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("✅ Attendance submitted successfully!");
      setAttendanceForm({});
      fetchAttendance();
    } catch (err) {
      console.error(err);
      alert("❌ Failed to submit attendance");
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={{ textAlign: "center" }}>HR Panel</h2>
        <div style={styles.sidebarItem(selected === "employees")} onClick={() => setSelected("employees")}>Employees</div>
        <div style={styles.sidebarItem(selected === "doctors")} onClick={() => setSelected("doctors")}>Doctors</div>
        <div style={styles.sidebarItem(selected === "attendance")} onClick={() => setSelected("attendance")}>Attendance</div>
        <div style={styles.sidebarItem(selected === "appointments")} onClick={() => setSelected("appointments")}>Appointments</div>
        <button style={styles.logoutBtn} onClick={onLogout}>Logout</button>
      </div>

      {/* Content */}
      <div style={styles.content}>
        {/* Employees */}
        {selected === "employees" && (
          <div>
            <div style={styles.total}>Total Employees: {employees.length}</div>
            {employees.map(emp => (
              <div key={emp._id} style={styles.card}>
                <p><strong>Name:</strong> {emp.name}</p>
                <p><strong>Email:</strong> {emp.email}</p>
                <p><strong>Role:</strong> {emp.role}</p>
              </div>
            ))}
          </div>
        )}

        {/* Doctors */}
        {selected === "doctors" && (
          <div>
            <div style={styles.total}>Total Doctors: {doctors.length}</div>
            {doctors.map(doc => (
              <div key={doc._id} style={styles.card}>
                <p><strong>Name:</strong> {doc.name}</p>
                <p><strong>Email:</strong> {doc.email}</p>
                <p><strong>Role:</strong> {doc.role}</p>
              </div>
            ))}
          </div>
        )}

        {/* Attendance */}
        {selected === "attendance" && (
          <div>
            <h3>Mark Attendance</h3>
            <div style={styles.total}>Total Employees: {employees.length}</div>

            {employees.map(emp => (
              <div key={emp._id} style={styles.card}>
                <p><strong>{emp.name}</strong> ({emp.email})</p>
                <button
                  style={{
                    ...styles.button,
                    background: attendanceForm[emp._id] === "present" ? "#28a745" : "#ccc",
                    color: "#fff",
                  }}
                  onClick={() => markAttendance(emp._id, "present")}
                >
                  Present
                </button>
                <button
                  style={{
                    ...styles.button,
                    background: attendanceForm[emp._id] === "absent" ? "#dc3545" : "#ccc",
                    color: "#fff",
                  }}
                  onClick={() => markAttendance(emp._id, "absent")}
                >
                  Absent
                </button>
              </div>
            ))}

            <button style={styles.submitBtn} onClick={submitAttendance}>Submit Attendance</button>

            <hr style={{ margin: "20px 0" }} />

            <h3>Previous Attendance Records</h3>
            {attendance.length === 0 ? <p>No attendance records yet</p> : (
              attendance.map(a => (
                <div key={a._id} style={styles.card}>
                  <p><strong>Date:</strong> {a.date}</p>
                  <p><strong>Employee:</strong> {a.employee?.name || "Unknown"}</p>
                  <p><strong>Status:</strong> {a.status}</p>
                </div>
              ))
            )}
          </div>
        )}

        {/* Appointments */}
        {selected === "appointments" && (
          <div>
            <h3>Appointments</h3>
            {appointments.map(a => (
              <div key={a._id} style={styles.card}>
                <p><strong>Employee:</strong> {a.employeeId?.name || "Unknown"}</p>
                <p><strong>Doctor:</strong> {a.doctorId?.name || "Unknown"}</p>
                <p><strong>Date & Time:</strong> {new Date(a.datetime).toLocaleString()}</p>
                <p><strong>Verified:</strong> {a.verified ? "Yes" : "No"}</p>
                {!a.verified && (
                  <button
                    style={{ ...styles.button, background: "#28a745", color: "#fff" }}
                    onClick={() => verifyAppointment(a._id)}
                  >
                    Verify
                  </button>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
