// src/dashboards/EmployeeDashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API || "http://localhost:5000/api";

export default function EmployeeDashboard({ user, onLogout }) {
  const [selected, setSelected] = useState("appointments");
  const [appointments, setAppointments] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [newAppointment, setNewAppointment] = useState({
    name: user.name || "",
    email: user.email || "",
    doctorId: "",
    date: "",
    reason: ""
  });

  useEffect(() => {
    fetchAppointments();
    fetchDoctors();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`${API}/appointments`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchDoctors = async () => {
    try {
      const res = await axios.get(`${API}/doctors`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setDoctors(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewAppointment(prev => ({ ...prev, [name]: value }));
  };

  const bookAppointment = async () => {
    if (!newAppointment.doctorId || !newAppointment.date || !newAppointment.reason) {
      return alert("Please fill all fields and select a doctor");
    }
    try {
      const res = await axios.post(
        `${API}/appointments`,
        {
          date: newAppointment.date,
          reason: newAppointment.reason,
          doctorId: newAppointment.doctorId,
        },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      setAppointments([...appointments, res.data]);
      setNewAppointment(prev => ({ ...prev, doctorId: "", date: "", reason: "" }));
      alert("Appointment booked!");
    } catch (err) {
      console.error(err.response || err);
      alert("Failed to book appointment");
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <div style={{ width: "220px", background: "#007bff", color: "#fff", padding: "20px" }}>
        <h2>Employee</h2>
        <button style={{ marginTop: "20px" }} onClick={onLogout}>Logout</button>
      </div>

      {/* Content */}
      <div style={{ flex: 1, padding: "20px", overflowY: "auto" }}>
        <h2>Appointments</h2>

        {/* Existing appointments */}
        {appointments.map(a => (
          <div key={a._id} style={{ background: "#eee", padding: "10px", margin: "10px 0", borderRadius: "5px" }}>
            <p><strong>Date:</strong> {new Date(a.date).toLocaleString()}</p>
            <p><strong>Reason:</strong> {a.reason}</p>
            <p><strong>Doctor:</strong> {a.doctor?.name || "N/A"}</p>
          </div>
        ))}

        {/* Book new appointment */}
        <div style={{ marginTop: "20px" }}>
          <input
            type="text"
            name="name"
            value={newAppointment.name}
            placeholder="Your Name"
            disabled
            style={{ padding: "10px", marginRight: "10px" }}
          />
          <input
            type="email"
            name="email"
            value={newAppointment.email}
            placeholder="Your Email"
            disabled
            style={{ padding: "10px", marginRight: "10px" }}
          />
          <select name="doctorId" value={newAppointment.doctorId} onChange={handleChange} style={{ padding: "10px", marginRight: "10px" }}>
            <option value="">-- Select Doctor --</option>
            {doctors.map(d => <option key={d._id} value={d._id}>{d.name}</option>)}
          </select>
          <input
            type="datetime-local"
            name="date"
            value={newAppointment.date}
            onChange={handleChange}
            style={{ padding: "10px", marginRight: "10px" }}
          />
          <input
            type="text"
            name="reason"
            value={newAppointment.reason}
            placeholder="Reason for appointment"
            onChange={handleChange}
            style={{ padding: "10px", marginRight: "10px" }}
          />
          <button onClick={bookAppointment} style={{ padding: "10px" }}>Book</button>
        </div>
      </div>
    </div>
  );
}
